package Data;

public class switch1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=30;
		
		switch(i) {
		case 10:System.out.println("Today is Monday");
				break;
		case 20:System.out.println("Today is Tuesday");
				break;
		case 30:System.out.println("Today is Wednesday");
				break;
		case 40:System.out.println("Today is Thursday");
				break;
		case 50:System.out.println("Today is Friday");
				break;
		case 60:System.out.println("Today is Saturday");
				break;
		default:System.out.println("Today is Sunday");
		}
	}

}
