package Data;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		while(i>=1) {
		System.out.println(10*i);
		i--;
		}
		int j=1,sum=0;
		while(j<=20) {
			sum=sum+j;
			j++;
		
		}
		System.out.println("Sum= "+sum);
		
		
	}

}
